import React, { Component } from 'react'

class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      inputText: '',
      inputEmail: '',
    };
  }

  // desestruturar o target de event.target
  handleChange({ target }) {
    // destructuring name de target.name
    const { name } = target;
    // operador ternario para caso o tipo do checkbox seja checbox, pegando o target.checked.
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <div>
        <h1>Estados e React - Tecnologia fant√°stica ou reagindo a regionalismos?</h1>
        <form className="form">
          <label>
            <select name="options" id="options">
              <option value="option">Option1</option>
              <option value="option">Option2</option>
            </select>
          </label>

          <label>
            <input value={this.state.inputText} onChange={this.handleChange} name="inputText" placeholder="input text" type="text" />
            <input value={this.state.inputEmail} name="inputEmail" placeholder="input email" type="email" />
          </label>

          <label>
            <textarea placeholder="textarea" name="textArea" cols="30" rows="10"></textarea>
          </label>
        </form>
      </div>
    );
  }
}

export default Form;
