import React, { Component } from 'react'
import Options from './Options';
import EmailAndText from './EmailAndText';
import TextArea from './TextArea';
class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      inputText: '',
      inputEmail: '',
      options: '',
      textArea: '',
      verification: false,
    };
  }

  // desestruturar o target de event.target
  handleChange({ target }) {
    // destructuring name de target.name
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <div>
        <h1>Estados e React - Tecnologia fant√°stica ou reagindo a regionalismos?</h1>
        <form className="form">
          <Options
            value={this.state.options}
            handleChange={this.handleChange}
          />

          <label>
            <input value={this.state.inputText} onChange={this.handleChange} name="inputText" placeholder="input text" type="text" />
            <input value={this.state.inputEmail} onChange={this.handleChange} name="inputEmail" placeholder="input email" type="email" />
            <input type="file" name="" id="" />
          </label>

          <TextArea handleChange={this.handleChange} value={this.state.textArea} />

          <label>
            <input type="checkbox" name="verification" value={this.state.verification} onChange={this.handleChange} />
          </label>

        </form>
      </div>
    );
  }
}

export default Form;
